import styled from "styled-components"

export const Container = styled.div`
    button {
        max-width: 18rem;
        height: 4.9rem;
        background-color: #7CA431;
        border-radius: 20px;
        cursor: pointer;
        border: none;
        color: white;
        font-size: 24px;
        font-weight: bold;
    }
`;
