import styled from "styled-components"

export const Container = styled.div`
    max-width: 20rem;
    width: 100%;
    max-height: 14.5rem;
    height: 100vw;
    margin-bottom: 2rem;
    border: 1px solid green;

    img {
        width: 100%;
        height: 9.5vw;
        background-color: #ccc;
        max-height: 12rem;
    }

    div.text {
        display: flex;
        color: white;
        font-size: 18px;
        margin: 2% 0 0 7%;

        svg {
            margin-right: 0.4rem;
        }
    }

    @media screen and (max-width: 1840px) {
        width: 45%;
        height: 12vw;

    }
`;
