import { Container } from './styles'
import { useDynimicityContext } from '../../Context/useDynimicityContext.js'
import { useEffect } from 'react';

const Game = ({nome, photo, index}) => {

    const {setGameIndex, selectedEffects, gameIndex} = useDynimicityContext();

    useEffect(() => {
        /*api.get(`/games/show/1`).then((response) => {
          console.log(response.data)
          setGame(response.data)
        })*/
        console.log(gameIndex)
    }, [gameIndex]);

    return (
        <Container onClick={() => {setGameIndex(index); console.log(gameIndex)}} >
            <div className={selectedEffects(index)}>
                <img alf="capa" src={`http://localhost:3000${photo}`}/>

                <div className="text">
                    <svg width="14" height="18" viewBox="0 0 14 18" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M1 1L13 8.71428L1 16.4286V1Z" stroke="#7CA431" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>

                    <p>{nome}</p>
                </div>
            </div>
        </Container>
    )
}

export default Game
