import styled from "styled-components"

export const Container = styled.div`
    display: inline-block;
    width: 33%;
    height: 31rem;
    margin-left: 10%;

    img {
        width: 100%;
        height: 48%;
        background-color: #ccc;
    }

    div {
        padding: 0 9%;

        svg {
            display: inline-block;
        }

        h1 {
            display: inline-block;
            font-size: 24px;
            color: white;
            text-align: right;
        }
    }
`;
